// +build gpu
package pow

import (
	"github.com/iotaledger/giota/trinary"

	"testing"
	"time"
)

func TestPowCL(t *testing.T) {
	var transaction trinary.Trytes = ""

	s := time.Now()
	nonce, err := PowCL(transaction, transaction.DefaultMinWeightMagnitude)
	ti := time.Now().Sub(s)
	if err != nil {
		t.Fatal(err)
	}

	transaction = transaction[:len(transaction)-transaction.NonceTrinarySize/3] + nonce
	h := transaction.Hash()
	if h[len(h)-5:] != "99999" {
		t.Error("pow is illegal", h)
	}

	sp := float64(countCL) / 1000 / ti.Seconds()
	t.Logf("%d kH/sec on OpenCL PoW", int(sp))
}
