func testPowGo(t *testing.T) {
	var txTrytes trinary.Trytes = ""

	nonce, err := PowGo(txTrytes, 14)
	if err != nil {
		t.Fatal(err)
	}

	txTrytes = txTrytes[:len(txTrytes)-transaction.NonceTrinarySize/3] + nonce
	h := curl.Hash(txTrytes)
	if h[len(h)-4:] != "9999" {
		t.Error("pow is illegal", h)
	}
}

